{extend name="public/container"}{block name="content"}<div class="row">    <div class="col-sm-12">        <div class="ibox float-e-margins">            <div class="ibox-title">                <button type="button" class="btn btn-w-m btn-primary add-filed">添加文章</button>            </div>            <div class="ibox-content">                <div class="row">                    <div class="m-b m-l">                        <form action="" class="form-inline">                            <select name="consult_type" aria-controls="editable" class="form-control input-sm" style="padding-top: 4px;">                                <option value="0">全部</option>                                <option value="1" {eq name="$where.consult_type" value="1"}selected="selected"{/eq}>图片</option>                                <option value="2" {eq name="$where.consult_type" value="2"}selected="selected"{/eq}>视屏</option>                            </select>                            <div class="input-group" style="margin-top: 5px">                                <input type="text" placeholder="请输入关键词" class="input-sm form-control" name="title" value="{$where.title}">                                <span class="input-group-btn"><button type="submit" class="btn btn-sm btn-primary"> <i class="fa fa-search" ></i>搜索</button> </span>                            </div>                        </form>                    </div>                </div>                <div class="table-responsive">                    <table class="table table-striped  table-bordered">                        <thead>                        <tr>                            <th class="text-center">编号</th>                            <th class="text-center">文章标题</th>                            <th class="text-center">状态</th>                            <th class="text-center">浏览量</th>                            <th class="text-center">排序</th>                            <th class="text-center">查看文章</th>                            <th class="text-center">操作</th>                        </tr>                        </thead>                        <tbody class="">                        {volist name="list" id="vo"}                        <tr>                            <td class="text-center">                                {$vo.id}                            </td>                            <td class="text-center">                                {$vo.title}                            </td>                            <td class="text-center">                                {if condition="$vo['status'] eq 1"}                                <i class="fa fa-check text-navy"></i>                                {else/}                                <i class="fa fa-close text-danger"></i>                                {/if}                            </td>                            <td class="text-center">                                {$vo.visit}                            </td>                            <td class="text-center">                                {$vo.sort}                            </td>                            <td class="text-center">                                <button type="button" class="btn btn-xs btn-primary" onclick="$eb.createModalFrame(this.innerText,'{:Url('edit_content',array('id'=>$vo['id'],'type'=>'content'))}')"><i class="fa fa-pencil"></i> 编辑内容</button>                            </td>                            <td class="text-center">                                <button class="btn btn-info btn-xs" type="button"  onclick="$eb.createModalFrame('编辑','{:Url('edit',array('id'=>$vo['id']))}')"><i class="fa fa-paste"></i> 编辑</button>                                <button class="btn btn-warning btn-xs del_config_tab" data-id="{$vo.id}" type="button" data-url="{:Url('delete',array('id'=>$vo['id']))}" ><i class="fa fa-warning"></i> 删除                                </button>                            </td>                        </tr>                        {/volist}                        </tbody>                    </table>                </div>                {include file="public/inner_page"}            </div>        </div>    </div></div>{/block}{block name="script"}<script>    $('.add-filed').on('click',function (e) {        $eb.createModalFrame(this.innerText,"{:Url('create')}");    })    $('.del_config_tab').on('click',function(){        var _this = $(this),url =_this.data('url');        $eb.$swal('delete',function(){            $eb.axios.get(url).then(function(res){                if(res.status == 200 && res.data.code == 200) {                    $eb.$swal('success',res.data.msg);                    _this.parents('tr').remove();                }else                    return Promise.reject(res.data.msg || '删除失败')            }).catch(function(err){                $eb.$swal('error',err);            });        })    });</script>{/block}